client/src/components/Logo.tsx.content:
import logoImage from "@assets/WhatsApp_Image_2025-07-27_at_19.32.23_526d3b67-removebg-preview_1755354631035.png";

interface LogoProps {
  className?: string;
  size?: "sm" | "md" | "lg";
}

export default function Logo({ className = "", size = "md" }: LogoProps) {
  const sizeConfig = {
    sm: { height: "28" },
    md: { height: "36" },
    lg: { height: "56" }
  };

  const { height } = sizeConfig[size];

  return (
    <img 
      src={logoImage} 
      alt="INSTAURA Logo" 
      style={{ height: `${height}px` }}
      className={`object-contain ${className}`}
      data-testid="logo-image"
    />
  );
}

