                <div className="block px-3 py-2 text-brand-black hover:text-brand-red transition-colors duration-300">
                  Home
                </div>
              </Link>
              {navItems.map((item) => (
                <Link
                  key={item.href}
                  href={item.href}
                  onClick={() => setIsMobileMenuOpen(false)}
                  data-testid={`mobile-nav-link-${item.label.toLowerCase().replace(" ", "-")}`}
                >
                  <div className="block px-3 py-2 text-brand-black hover:text-brand-red transition-colors duration-300">
                    {item.label}
                  </div>
                </Link>
              ))}
              <div className="px-3 pt-4">
                <Link href="/contact" onClick={() => setIsMobileMenuOpen(false)}>
                  <Button className="w-full bg-brand-red text-white hover:bg-red-700 rounded-full font-semibold">
                    Talk to YAS
                  </Button>
